"use client"

import { useState } from "react"
import { Button } from "@/components/ui/button"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { ChefHat, CheckCircle } from "lucide-react"
import { useRouter } from "next/navigation"

export default function SetupPage() {
  const [isLoading, setIsLoading] = useState(false)
  const [isComplete, setIsComplete] = useState(false)
  const [error, setError] = useState<string | null>(null)
  const router = useRouter()

  const createAdminUser = async () => {
    setIsLoading(true)
    setError(null)

    try {
      console.log("[v0] Creating admin staff...")

      const response = await fetch("/api/auth/setup", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          email: "admin@restaurant.com",
          password: "0919RW",
          first_name: "Admin",
          last_name: "User",
          staff_id: "2009AU"
        }),
      })

      const result = await response.json()

      if (!response.ok) {
        throw new Error(result.error || "Failed to create admin staff")
      }

      console.log("[v0] Admin staff created successfully:", result)

      setIsComplete(true)

      setTimeout(() => {
        router.push("/auth/login")
      }, 3000)
    } catch (error: any) {
      console.log("[v0] Setup error:", error)
      setError(error.message || "Failed to create admin staff")
