components/admin/account-creation.tsx.content:
"use client"

import type React from "react"

import { useState } from "react"
import { createClient } from "@/lib/supabase/client"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Label } from "@/components/ui/label"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select"
import { Alert, AlertDescription } from "@/components/ui/alert"
import { UserPlus, Loader2, CheckCircle, AlertCircle } from "lucide-react"

interface CreateAccountForm {
  email: string
  username: string
  password: string
  firstName: string
  lastName: string
  role: "admin" | "waiter"
}

export function AccountCreation() {
  const [form, setForm] = useState<CreateAccountForm>({
    email: "",
    username: "",
    password: "",
    firstName: "",
    lastName: "",
    role: "waiter",
  })
  const [loading, setLoading] = useState(false)
  const [message, setMessage] = useState<{ type: "success" | "error"; text: string } | null>(null)

  const supabase = createClient()

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    setLoading(true)
    setMessage(null)

    try {
      // Create user in Supabase Auth
      const { data: authData, error: authError } = await supabase.auth.signUp({
        email: form.email,
        password: form.password,
        options: {
          data: {
            username: form.username,
            first_name: form.firstName,
            last_name: form.lastName,
            role: form.role,
          },
        },
      })

      if (authError) throw authError

      if (authData.user) {
        // Create user record in our users table
        const { error: dbError } = await supabase.from("users").insert({
          id: authData.user.id,
          username: form.username,
          email: form.email,
          first_name: form.firstName,
          last_name: form.lastName,
          role: form.role,
          is_active: true,
        })

        if (dbError) throw dbError

        setMessage({
          type: "success",
          text: `Account created successfully for ${form.firstName} ${form.lastName}`,
        })

        // Reset form
        setForm({
          email: "",
          username: "",
          password: "",
          firstName: "",
          lastName: "",
          role: "waiter",
        })
      }
    } catch (error: any) {
      setMessage({
        type: "error",
        text: error.message || "Failed to create account",
      })
    } finally {
      setLoading(false)
    }
  }

  const handleInputChange = (field: keyof CreateAccountForm, value: string) => {
    setForm((prev) => ({ ...prev, [field]: value }))
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center gap-3">
        <UserPlus className="w-6 h-6 text-primary" />
        <h2 className="text-2xl font-bold">Create Staff Account</h2>
      </div>

      <Card className="bg-card/50 backdrop-blur-sm border-border">
        <CardHeader>
          <CardTitle>New Staff Member</CardTitle>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="firstName">First Name</Label>
                <Input
                  id="firstName"
                  value={form.firstName}
                  onChange={(e) => handleInputChange("firstName", e.target.value)}
                  required
                  className="bg-background/50"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="lastName">Last Name</Label>
                <Input
                  id="lastName"
                  value={form.lastName}
                  onChange={(e) => handleInputChange("lastName", e.target.value)}
                  required
                  className="bg-background/50"
                />
              </div>
            </div>

            <div className="space-y-2">
              <Label htmlFor="email">Email</Label>
              <Input
                id="email"
                type="email"
                value={form.email}
                onChange={(e) => handleInputChange("email", e.target.value)}
                required
                className="bg-background/50"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="username">Username</Label>
              <Input
                id="username"
                value={form.username}
                onChange={(e) => handleInputChange("username", e.target.value)}
                required
                className="bg-background/50"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="password">Password</Label>
              <Input
                id="password"
                type="password"
                value={form.password}
                onChange={(e) => handleInputChange("password", e.target.value)}
                required
                minLength={6}
                className="bg-background/50"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="role">Role</Label>
              <Select value={form.role} onValueChange={(value: "admin" | "waiter") => handleInputChange("role", value)}>
                <SelectTrigger className="bg-background/50">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="waiter">Waiter</SelectItem>
                  <SelectItem value="admin">Admin</SelectItem>
                </SelectContent>
              </Select>
            </div>

            {message && (
              <Alert
                className={
                  message.type === "success" ? "border-green-500/50 bg-green-500/10" : "border-red-500/50 bg-red-500/10"
                }
              >
                {message.type === "success" ? (
                  <CheckCircle className="h-4 w-4 text-green-400" />
                ) : (
                  <AlertCircle className="h-4 w-4 text-red-400" />
                )}
                <AlertDescription className={message.type === "success" ? "text-green-400" : "text-red-400"}>
                  {message.text}
                </AlertDescription>
              </Alert>
            )}

            <Button type="submit" disabled={loading} className="w-full">
              {loading ? (
                <>
                  <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                  Creating Account...
                </>
              ) : (
                <>
                  <UserPlus className="w-4 h-4 mr-2" />
                  Create Account
                </>
              )}
            </Button>
          </form>
        </CardContent>
      </Card>
    </div>
  )
}

components/admin/menu-management.tsx.content:
"use client"

import type React from "react"

import { useState, useEffect } from "react"
import { createClient } from "@/lib/supabase/client"
import { Button } from "@/components/ui/button"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"
import { Input } from "@/components/ui/input"
import { Label } from "@/components/ui/label"
import { Textarea } from "@/components/ui/textarea"
import { Badge } from "@/components/ui/badge"
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog"
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select"
import { Switch } from "@/components/ui/switch"
import { Plus, Edit, Trash2, Utensils } from "lucide-react"
import { useToast } from "@/hooks/use-toast"

interface MenuItem {
  id: string
  name: string
  description: string
  price: number
  category: string
  image_url?: string
  is_available: boolean
  preparation_time: number
  ingredients?: string[]
  allergens?: string[]
}

export function MenuManagement() {
  const [menuItems, setMenuItems] = useState<MenuItem[]>([])
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false)
  const [editingItem, setEditingItem] = useState<MenuItem | null>(null)
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    price: "",
    category: "",
    preparation_time: "15",
    is_available: true,
  })

  const supabase = createClient()
  const { toast } = useToast()

  const categories = ["Pizza", "Salads", "Main Course", "Desserts", "Beverages", "Appetizers"]

  useEffect(() => {
    fetchMenuItems()
  }, [])

  const fetchMenuItems = async () => {
    try {
      const { data, error } = await supabase.from("menu_items").select("*").order("category", { ascending: true })

      if (error) throw error
      setMenuItems(data || [])
    } catch (error) {
      console.error("Error fetching menu items:", error)
    }
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()

    try {
      const itemData = {
        name: formData.name,
        description: formData.description,
        price: Number.parseFloat(formData.price),
        category: formData.category,
        preparation_time: Number.parseInt(formData.preparation_time),
        is_available: formData.is_available,
      }

      if (editingItem) {
        const { error } = await supabase.from("menu_items").update(itemData).eq("id", editingItem.id)

        if (error) throw error

        toast({
          title: "Menu Item Updated",
          description: "Menu item has been updated successfully.",
        })
      } else {
        const { error } = await supabase.from("menu_items").insert(itemData)

        if (error) throw error

        toast({
          title: "Menu Item Added",
          description: "New menu item has been added successfully.",
        })
      }

      setFormData({
        name: "",
        description: "",
        price: "",
        category: "",
        preparation_time: "15",
        is_available: true,
      })
      setIsAddDialogOpen(false)
      setEditingItem(null)
      fetchMenuItems()
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to save menu item.",
        variant: "destructive",
      })
    }
  }

  const handleEdit = (item: MenuItem) => {
    setEditingItem(item)
    setFormData({
      name: item.name,
      description: item.description,
      price: item.price.toString(),
      category: item.category,
      preparation_time: item.preparation_time.toString(),
      is_available: item.is_available,
    })
    setIsAddDialogOpen(true)
  }

  const handleDelete = async (itemId: string) => {
    if (!confirm("Are you sure you want to delete this menu item?")) return

    try {
      const { error } = await supabase.from("menu_items").delete().eq("id", itemId)

      if (error) throw error

      toast({
        title: "Menu Item Deleted",
        description: "Menu item has been deleted successfully.",
      })

      fetchMenuItems()
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to delete menu item.",
        variant: "destructive",
      })
    }
  }

  const toggleAvailability = async (itemId: string, currentStatus: boolean) => {
    try {
      const { error } = await supabase.from("menu_items").update({ is_available: !currentStatus }).eq("id", itemId)

      if (error) throw error

      toast({
        title: "Availability Updated",
        description: `Menu item is now ${!currentStatus ? "available" : "unavailable"}.`,
      })

      fetchMenuItems()
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to update availability.",
        variant: "destructive",
      })
    }
  }

  const groupedItems = menuItems.reduce(
    (acc, item) => {
      if (!acc[item.category]) {
        acc[item.category] = []
      }
      acc[item.category].push(item)
      return acc
    },
    {} as Record<string, MenuItem[]>,
  )

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold">Menu Management</h2>
          <p className="text-muted-foreground">Manage restaurant menu items and categories</p>
        </div>
        <Dialog open={isAddDialogOpen} onOpenChange={setIsAddDialogOpen}>
          <DialogTrigger asChild>
            <Button className="bg-primary hover:bg-primary/90">
              <Plus className="w-4 h-4 mr-2" />
              Add Menu Item
            </Button>
          </DialogTrigger>
          <DialogContent className="sm:max-w-md">
            <DialogHeader>
              <DialogTitle>{editingItem ? "Edit Menu Item" : "Add New Menu Item"}</DialogTitle>
            </DialogHeader>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <Label htmlFor="name">Item Name</Label>
                <Input
                  id="name"
                  value={formData.name}
                  onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                  required
                />
              </div>
              <div>
                <Label htmlFor="description">Description</Label>
                <Textarea
                  id="description"
                  value={formData.description}
                  onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                  rows={3}
                />
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="price">Price ($)</Label>
                  <Input
                    id="price"
                    type="number"
                    step="0.01"
                    min="0"
                    value={formData.price}
                    onChange={(e) => setFormData({ ...formData, price: e.target.value })}
                    required
                  />
                </div>
                <div>
                  <Label htmlFor="prep_time">Prep Time (min)</Label>
                  <Input
                    id="prep_time"
                    type="number"
                    min="1"
                    value={formData.preparation_time}
                    onChange={(e) => setFormData({ ...formData, preparation_time: e.target.value })}
                    required
                  />
                </div>
              </div>
              <div>
                <Label htmlFor="category">Category</Label>
                <Select
                  value={formData.category}
                  onValueChange={(value) => setFormData({ ...formData, category: value })}
                >
                  <SelectTrigger>
                    <SelectValue placeholder="Select category" />
                  </SelectTrigger>
                  <SelectContent>
                    {categories.map((category) => (
                      <SelectItem key={category} value={category}>
                        {category}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
              <div className="flex items-center space-x-2">
                <Switch
                  id="available"
                  checked={formData.is_available}
                  onCheckedChange={(checked) => setFormData({ ...formData, is_available: checked })}
                />
                <Label htmlFor="available">Available</Label>
              </div>
              <div className="flex gap-2">
                <Button type="submit" className="flex-1">
                  {editingItem ? "Update" : "Add"} Item
                </Button>
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => {
                    setIsAddDialogOpen(false)
                    setEditingItem(null)
                    setFormData({
                      name: "",
                      description: "",
                      price: "",
                      category: "",
                      preparation_time: "15",
                      is_available: true,
                    })
                  }}
                >
                  Cancel
                </Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      <div className="space-y-6">
        {Object.entries(groupedItems).map(([category, items]) => (
          <Card key={category} className="bg-card/50 backdrop-blur-sm border-border">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Utensils className="w-5 h-5" />
                {category}
                <Badge variant="secondary">{items.length} items</Badge>
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {items.map((item) => (
                  <Card key={item.id} className="bg-background/50">
                    <CardContent className="p-4">
                      <div className="flex items-start justify-between mb-2">
                        <h4 className="font-semibold">{item.name}</h4>
                        <Badge
                          variant={item.is_available ? "default" : "secondary"}
                          className={
                            item.is_available ? "bg-green-500/20 text-green-400" : "bg-red-500/20 text-red-400"
                          }
                        >
                          {item.is_available ? "Available" : "Unavailable"}
                        </Badge>
                      </div>
                      <p className="text-sm text-muted-foreground mb-3 line-clamp-2">{item.description}</p>
                      <div className="flex items-center justify-between mb-3">
                        <span className="font-bold text-primary">${item.price.toFixed(2)}</span>
                        <span className="text-xs text-muted-foreground">{item.preparation_time} min</span>
                      </div>
                      <div className="flex gap-2">
                        <Button variant="ghost" size="sm" onClick={() => handleEdit(item)}>
                          <Edit className="w-3 h-3" />
                        </Button>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => toggleAvailability(item.id, item.is_available)}
                          className={
                            item.is_available
                              ? "text-red-500 hover:text-red-600"
                              : "text-green-500 hover:text-green-600"
                          }
                        >
                          {item.is_available ? "Disable" : "Enable"}
                        </Button>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => handleDelete(item.id)}
                          className="text-red-500 hover:text-red-600"
                        >
                          <Trash2 className="w-3 h-3" />
                        </Button>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  )
}

components/admin/orders-overview.tsx.content:
"use client"

import { useState, useEffect } from "react"
import { createClient } from "@/lib/supabase/client"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"
import { Badge } from "@/components/ui/badge"
import { Button } from "@/components/ui/button"
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select"
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table"
import { ReceiptButton } from "../ui/receipt-button"
import { Clock, User, MapPin, DollarSign } from "lucide-react"
import { useToast } from "@/hooks/use-toast"

interface Order {
  id: string
  table_number: number
  customer_name?: string
  status: string
  total_amount: number
  created_at: string
  waiter: {
    first_name: string
    last_name: string
  }
  order_items: {
    id: string
    quantity: number
    menu_item: {
      name: string
    }
  }[]
}

export function OrdersOverview() {
  const [orders, setOrders] = useState<Order[]>([])
  const [statusFilter, setStatusFilter] = useState("all")
  const [loading, setLoading] = useState(true)

  const supabase = createClient()
  const { toast } = useToast()

  useEffect(() => {
    fetchOrders()

    // Set up real-time subscription
    const subscription = supabase
      .channel("admin_orders")
      .on("postgres_changes", { event: "*", schema: "public", table: "orders" }, () => fetchOrders())
      .subscribe()

    return () => {
      subscription.unsubscribe()
    }
  }, [])

  const fetchOrders = async () => {
    try {
      const { data, error } = await supabase
        .from("orders")
        .select(`
          *,
          waiter:users!orders_waiter_id_fkey (
            first_name,
            last_name
          ),
          order_items (
            id,
            quantity,
            menu_item:menu_items (
              name
            )
          )
        `)
        .order("created_at", { ascending: false })

      if (error) throw error
      setOrders(data || [])
    } catch (error) {
      console.error("Error fetching orders:", error)
    } finally {
      setLoading(false)
    }
  }

  const updateOrderStatus = async (orderId: string, newStatus: string) => {
    try {
      const { error } = await supabase.from("orders").update({ status: newStatus }).eq("id", orderId)

      if (error) throw error

      toast({
        title: "Order Updated",
        description: `Order status changed to ${newStatus}.`,
      })

      fetchOrders()
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to update order status.",
        variant: "destructive",
      })
    }
  }

  const getStatusColor = (status: string) => {
    switch (status) {
      case "pending":
        return "bg-yellow-500/20 text-yellow-400"
      case "confirmed":
        return "bg-blue-500/20 text-blue-400"
      case "preparing":
        return "bg-orange-500/20 text-orange-400"
      case "ready":
        return "bg-green-500/20 text-green-400"
      case "served":
        return "bg-purple-500/20 text-purple-400"
      case "paid":
        return "bg-green-600/20 text-green-300"
      case "cancelled":
        return "bg-red-500/20 text-red-400"
      default:
        return "bg-gray-500/20 text-gray-400"
    }
  }

  const getTimeAgo = (dateString: string) => {
    const now = new Date()
    const orderTime = new Date(dateString)
    const diffInMinutes = Math.floor((now.getTime() - orderTime.getTime()) / (1000 * 60))

    if (diffInMinutes < 1) return "Just now"
    if (diffInMinutes < 60) return `${diffInMinutes}m ago`
    const hours = Math.floor(diffInMinutes / 60)
    return `${hours}h ${diffInMinutes % 60}m ago`
  }

  const filteredOrders = orders.filter((order) => statusFilter === "all" || order.status === statusFilter)

  const statusOptions = [
    { value: "all", label: "All Orders" },
    { value: "pending", label: "Pending" },
    { value: "confirmed", label: "Confirmed" },
    { value: "preparing", label: "Preparing" },
    { value: "ready", label: "Ready" },
    { value: "served", label: "Served" },
    { value: "paid", label: "Paid" },
    { value: "cancelled", label: "Cancelled" },
  ]

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-muted-foreground">Loading orders...</div>
      </div>
    )
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold">Orders Overview</h2>
          <p className="text-muted-foreground">Monitor and manage all restaurant orders</p>
        </div>
        <Select value={statusFilter} onValueChange={setStatusFilter}>
          <SelectTrigger className="w-48">
            <SelectValue />
          </SelectTrigger>
          <SelectContent>
            {statusOptions.map((option) => (
              <SelectItem key={option.value} value={option.value}>
                {option.label}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>
      </div>

      <Card className="bg-card/50 backdrop-blur-sm border-border">
        <CardHeader>
          <CardTitle>Recent Orders ({filteredOrders.length})</CardTitle>
        </CardHeader>
        <CardContent>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Order Details</TableHead>
                <TableHead>Items</TableHead>
                <TableHead>Waiter</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Total</TableHead>
                <TableHead>Time</TableHead>
                <TableHead>Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {filteredOrders.map((order) => (
                <TableRow key={order.id}>
                  <TableCell>
                    <div className="space-y-1">
                      <div className="flex items-center gap-2">
                        <MapPin className="w-3 h-3 text-muted-foreground" />
                        <span className="font-medium">Table {order.table_number}</span>
                      </div>
                      {order.customer_name && (
                        <div className="flex items-center gap-2 text-sm text-muted-foreground">
                          <User className="w-3 h-3" />
                          {order.customer_name}
                        </div>
                      )}
                    </div>
                  </TableCell>
                  <TableCell>
                    <div className="text-sm">
                      {order.order_items.slice(0, 2).map((item, index) => (
                        <div key={item.id}>
                          {item.quantity}x {item.menu_item.name}
                        </div>
                      ))}
                      {order.order_items.length > 2 && (
                        <div className="text-muted-foreground">+{order.order_items.length - 2} more items</div>
                      )}
                    </div>
                  </TableCell>
                  <TableCell>
                    <div className="text-sm">
                      {order.waiter.first_name} {order.waiter.last_name}
                    </div>
                  </TableCell>
                  <TableCell>
                    <Badge className={getStatusColor(order.status)}>{order.status}</Badge>
                  </TableCell>
                  <TableCell>
                    <div className="flex items-center gap-1">
                      <DollarSign className="w-3 h-3" />
                      {order.total_amount.toFixed(2)}
                    </div>
                  </TableCell>
                  <TableCell>
                    <div className="flex items-center gap-1 text-sm text-muted-foreground">
                      <Clock className="w-3 h-3" />
                      {getTimeAgo(order.created_at)}
                    </div>
                  </TableCell>
                  <TableCell>
                    <div className="flex gap-1">
                      <ReceiptButton orderId={order.id} />
                      {order.status === "pending" && (
                        <Button size="sm" variant="outline" onClick={() => updateOrderStatus(order.id, "confirmed")}>
                          Confirm
                        </Button>
                      )}
                      {order.status === "confirmed" && (
                        <Button size="sm" variant="outline" onClick={() => updateOrderStatus(order.id, "preparing")}>
                          Prepare
                        </Button>
                      )}
                      {order.status === "preparing" && (
                        <Button size="sm" variant="outline" onClick={() => updateOrderStatus(order.id, "ready")}>
                          Ready
                        </Button>
                      )}
                      {order.status === "served" && (
                        <Button size="sm" variant="outline" onClick={() => updateOrderStatus(order.id, "paid")}>
                          Mark Paid
                        </Button>
                      )}
                    </div>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </CardContent>
      </Card>
    </div>
  )
}

components/admin/staff-management.tsx.content:
"use client"

import type React from "react"

import { useState, useEffect } from "react"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Label } from "@/components/ui/label"
import { Badge } from "@/components/ui/badge"
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog"
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table"
import { Plus, UserX } from "lucide-react"
import { createClient } from "@/lib/supabase/client"

interface Staff {
  staff_id: string
  first_name: string
  last_name: string
  email?: string
  created_at: string
  status: string
}

export default function StaffManagement() {
  const [staff, setStaff] = useState<Staff[]>([])
  const [isLoading, setIsLoading] = useState(true)
  const [isDialogOpen, setIsDialogOpen] = useState(false)
  const [formData, setFormData] = useState({
    first_name: "",
    last_name: "",
    email: "",
  })

  const generateStaffId = (firstName: string, lastName: string) => {
    const now = new Date()
    const day = now.getDate().toString().padStart(2, "0")
    const month = (now.getMonth() + 1).toString().padStart(2, "0")
    const firstLetter = firstName.charAt(0).toUpperCase()
    const lastLetter = lastName.charAt(0).toUpperCase()
    return `${day}${month}${firstLetter}${lastLetter}`
  }

  const fetchStaff = async () => {
    try {
      const { data, error } = await createClient().from("staff").select("*").order("created_at", { ascending: false })

      if (error) {
        console.error("Error fetching staff:", error)
        return
      }

      setStaff(data || [])
    } catch (error) {
      console.error("Error:", error)
    } finally {
      setIsLoading(false)
    }
  }

  const handleCreateStaff = async (e: React.FormEvent) => {
  e.preventDefault()

  const staffId = generateStaffId(formData.first_name, formData.last_name)

  try {
    // Get the current user
    const { data: { user } } = await createClient().auth.getUser()

    const { data, error } = await createClient()
      .from("staff")
      .insert([
        {
          staff_id: staffId,
          first_name: formData.first_name,
          last_name: formData.last_name,
          email: formData.email || null,
          created_by: user?.id, // Use the actual user UUID
          status: "active",
        },
      ])
      .select()

    if (error) {
      console.error("Error creating staff:", error)
      return
    }

    setFormData({ first_name: "", last_name: "", email: "" })
    setIsDialogOpen(false)
    fetchStaff()
  } catch (error) {
    console.error("Error:", error)
  }
}

  const handleToggleActive = async (staffId: string, currentStatus: string) => {
    try {
      const newStatus = currentStatus === "active" ? "inactive" : "active"
      const { error } = await createClient().from("staff").update({ status: newStatus }).eq("staff_id", staffId)

      if (error) {
        console.error("Error updating staff:", error)
        return
      }

      fetchStaff()
    } catch (error) {
      console.error("Error:", error)
    }
  }

  useEffect(() => {
    fetchStaff()
  }, [])

  if (isLoading) {
    return <div className="p-6">Loading staff...</div>
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h3 className="text-lg font-semibold">Staff Management</h3>
          <p className="text-sm text-muted-foreground">Create and manage staff accounts with auto-generated IDs</p>
        </div>

        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
          <DialogTrigger asChild>
            <Button className="gap-2">
              <Plus className="h-4 w-4" />
              Add Staff Member
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Add New Staff Member</DialogTitle>
            </DialogHeader>
            <form onSubmit={handleCreateStaff} className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="first_name">First Name</Label>
                <Input
                  id="first_name"
                  value={formData.first_name}
                  onChange={(e) => setFormData({ ...formData, first_name: e.target.value })}
                  required
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="last_name">Last Name</Label>
                <Input
                  id="last_name"
                  value={formData.last_name}
                  onChange={(e) => setFormData({ ...formData, last_name: e.target.value })}
                  required
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="email">Email (Optional)</Label>
                <Input
                  id="email"
                  type="email"
                  value={formData.email}
                  onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                />
              </div>
              {formData.first_name && formData.last_name && (
                <div className="p-3 bg-muted rounded-md">
                  <p className="text-sm font-medium">Generated Staff ID:</p>
                  <p className="text-lg font-mono">{generateStaffId(formData.first_name, formData.last_name)}</p>
                </div>
              )}
              <div className="flex gap-2">
                <Button type="submit" className="flex-1">
                  Create Staff Member
                </Button>
                <Button type="button" variant="outline" onClick={() => setIsDialogOpen(false)}>
                  Cancel
                </Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>Staff Members ({staff.length})</CardTitle>
        </CardHeader>
        <CardContent>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Staff ID</TableHead>
                <TableHead>Name</TableHead>
                <TableHead>Email</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Created</TableHead>
                <TableHead>Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {staff.map((member) => (
                <TableRow key={member.staff_id}>
                  <TableCell className="font-mono">{member.staff_id}</TableCell>
                  <TableCell>
                    {member.first_name} {member.last_name}
                  </TableCell>
                  <TableCell>{member.email || ""}</TableCell>
                  <TableCell>
                    <Badge variant={member.status === "active" ? "default" : "secondary"}>
                      {member.status === "active" ? "Active" : "Inactive"}
                    </Badge>
                  </TableCell>
                  <TableCell>{new Date(member.created_at).toLocaleDateString()}</TableCell>
                  <TableCell>
                    <div className="flex gap-2">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => handleToggleActive(member.staff_id, member.status)}
                      >
                        <UserX className="h-4 w-4" />
                        {member.status === "active" ? "Deactivate" : "Activate"}
                      </Button>
                    </div>
                  </TableCell>
                </TableRow>
              ))}
              {staff.length === 0 && (
                <TableRow>
                  <TableCell colSpan={6} className="text-center text-muted-foreground">
                    No staff members found. Add your first staff member to get started.
                  </TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
        </CardContent>
      </Card>
    </div>
  )
}

components/receipt/receipt-button.tsx.content:
"use client"

import { useState } from "react"
import { createClient } from "@/lib/supabase/client"
import { Button } from "@/components/ui/button"
import { ReceiptGenerator } from "./receipt-generator"
import { Receipt } from "lucide-react"

interface ReceiptButtonProps {
  orderId: string
  variant?: "default" | "outline" | "ghost"
  size?: "sm" | "default" | "lg"
}

export function ReceiptButton({ orderId, variant = "outline", size = "sm" }: ReceiptButtonProps) {
  const [order, setOrder] = useState<any>(null)
  const [loading, setLoading] = useState(false)
  const supabase = createClient()

  const fetchOrderDetails = async () => {
    if (loading || order) return

    setLoading(true)
    try {
      const { data, error } = await supabase
        .from("orders")
        .select(`
          *,
          waiter:users!orders_waiter_id_fkey (
            first_name,
            last_name
          ),
          order_items (
            *,
            menu_item:menu_items (
              name,
              description
            )
          )
        `)
        .eq("id", orderId)
        .single()

      if (error) throw error
      setOrder(data)
    } catch (error) {
      console.error("Error fetching order details:", error)
    } finally {
      setLoading(false)
    }
  }

  if (!order) {
    return (
      <Button variant={variant} size={size} onClick={fetchOrderDetails} disabled={loading}>
        <Receipt className="w-4 h-4 mr-2" />
        {loading ? "Loading..." : "Receipt"}
      </Button>
    )
  }

  return (
    <ReceiptGenerator
      order={order}
      trigger={
        <Button variant={variant} size={size}>
          <Receipt className="w-4 h-4 mr-2" />
          Receipt
        </Button>
      }
    />
  )
}

components/receipt/receipt-generator.tsx.content:
"use client"

import type React from "react"

import { useState } from "react"
import { Button } from "@/components/ui/button"
import { Card, CardContent } from "@/components/ui/card"
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog"
import { Receipt, Printer, Download, Eye } from "lucide-react"
import { useToast } from "@/hooks/use-toast"

interface OrderItem {
  id: string
  quantity: number
  unit_price: number
  total_price: number
  menu_item: {
    name: string
    description: string
  }
}

interface Order {
  id: string
  table_number: number
  customer_name?: string
  subtotal: number
  tax_amount: number
  total_amount: number
  payment_method?: string
  created_at: string
  waiter: {
    first_name: string
    last_name: string
  }
  order_items: OrderItem[]
}

interface ReceiptGeneratorProps {
  order: Order
  trigger?: React.ReactNode
}

export function ReceiptGenerator({ order, trigger }: ReceiptGeneratorProps) {
  const [isOpen, setIsOpen] = useState(false)
  const [isGenerating, setIsGenerating] = useState(false)
  const { toast } = useToast()

  const generateReceiptHTML = () => {
    const receiptDate = new Date(order.created_at).toLocaleString()

    return `
      <!DOCTYPE html>
      <html>
        <head>
          <title>Receipt - Order #${order.id.slice(-8)}</title>
          <style>
            body {
              font-family: 'Courier New', monospace;
              max-width: 300px;
              margin: 0 auto;
              padding: 20px;
              background: white;
              color: black;
            }
            .header {
              text-align: center;
              border-bottom: 2px solid #000;
              padding-bottom: 10px;
              margin-bottom: 15px;
            }
            .restaurant-name {
              font-size: 18px;
              font-weight: bold;
              margin-bottom: 5px;
            }
            .restaurant-info {
              font-size: 12px;
              margin-bottom: 2px;
            }
            .order-info {
              margin-bottom: 15px;
              font-size: 12px;
            }
